mapper.select:
select

from notice


   and BID = #{bid,jdbcType=INTEGER}


   AND CONTENT = #{content,jdbcType=VARCHAR}


   AND TITAL = #{tital,jdbcType=VARCHAR}


   AND CRIEATETIME = #{crieatetime,jdbcType=TIMESTAMP}


   AND USRENAME = #{username,jdbcType=VARCHAR}


   AND STATUS = #{status,jdbcType=VARCHAR}


    AND UID = #{uid,jdbcType=INTEGER}



BID = #{bid,jdbcType=INTEGER}

mapper.insert:
insert into notice (BID, CONTENT, TITAL, 
  CRIEATETIME, USERNAME, STATUS,UID
  )
values (#{bid,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, #{tital,jdbcType=VARCHAR}, 
  #{crieatetime,jdbcType=TIMESTAMP}, #{username,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR},#{uid,jdbcType=INTEGER}
  )
