mapper.select:
select

from resouces
where 1=1

  and RID =  #{rid,jdbcType=INTEGER}


  and TID =  #{tid,jdbcType=INTEGER}


  and RNAME =  #{rname,jdbcType=VARCHAR}


  and RDESCRIPTION =  #{rdescription,jdbcType=VARCHAR}


  and RQUANTITY =  #{rquantity,jdbcType=INTEGER}


  and BUYTIME =  #{buytime,jdbcType=TIMESTAMP}


  and VALIDITY =  #{validity,jdbcType=VARCHAR}


  and STATUS =  #{status,jdbcType=VARCHAR}


 and PICTURE = #{picture,jdbcType=VARCHAR}

mapper.insert:
insert into resouces (RID, TID, RNAME, 
  RDESCRIPTION, RQUANTITY, BUYTIME, 
  VALIDITY, STATUS, PICTURE
  )
values (#{rid,jdbcType=INTEGER}, #{tid,jdbcType=INTEGER}, #{rname,jdbcType=VARCHAR}, 
  #{rdescription,jdbcType=VARCHAR}, #{rquantity,jdbcType=INTEGER}, #{buytime,jdbcType=TIMESTAMP}, 
  #{validity,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{picture,jdbcType=VARCHAR}
  )
