mapper.select:
select  
from user
where  UNAME = #{uname,jdbcType=VARCHAR} and PASSWORD = #{password,jdbcType=VARCHAR}

select 
from user where 1=1


 and UID = #{uid,jdbcType=INTEGER}


  and UNAME = #{uname,jdbcType=VARCHAR}


  and PASSWORD = #{password,jdbcType=VARCHAR}


  and SEX = #{sex,jdbcType=VARCHAR}


  and EMAIL = #{email,jdbcType=VARCHAR}


  and IDENTITY = #{identity,jdbcType=VARCHAR}


  and PHONE = #{phone,jdbcType=VARCHAR}


  and STATUS = #{status,jdbcType=VARCHAR}


  and BIRTHDAY = #{birthday,jdbcType=VARCHAR}


  and SIGNATURE = #{signature,jdbcType=VARCHAR}


  and ADDRESS = #{address,jdbcType=VARCHAR}


  and REGISTRATIONTIME #{registrationtime,jdbcType=TIMESTAMP}


  and uid in

    #{item,jdbcType=INTEGER}

mapper.insert:
insert into user (UID, UNAME, PASSWORD, 
