  SIGNATURE, ADDRESS, REGISTRATIONTIME
  )
values (#{uid,jdbcType=INTEGER}, #{uname,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{sex,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{identity,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{birthday,jdbcType=VARCHAR}, 
  #{signature,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{registrationtime,jdbcType=TIMESTAMP}
  )
